// seatingConfig.js
export const seatingConfig = [
  { row: "A", seats: 18, category: "regular" },
  { row: "B", seats: 18, category: "regular" },
  { row: "C", seats: 18, category: "regular" },

  { row: "D", seats: 18, category: "bundle2" },
  { row: "E", seats: 18, category: "bundle4" },
  { row: "F", seats: 18, category: "bundle5" },

  // balcony gap
  { row: "gap1", seats: 0, category: "gap" },

  { row: "G", seats: 18, category: "regular" },

  // VIP rows later...
];
